import React from 'react';
import { Calendar, ChevronRight, Users, MessageSquare, CheckCircle } from 'lucide-react';

const RegistrationSection = () => {
  const steps = [
    {
      icon: <Users size={24} />,
      date: "21-24 Oktober 2024",
      title: "Open Recruitment",
      description: "Staff IMADIBA 2024/2025",
      status: "completed"
    },
    {
      icon: <Calendar size={24} />,
      date: "25 Oktober",
      title: "Pengumuman",
      description: "Lolos Berkas & Jadwal Wawancara",
      hasButton: true,
      status: "active"
    },
    {
      icon: <MessageSquare size={24} />,
      date: "26-27 Oktober",
      title: "Wawancara",
      description: "Tahap Seleksi Kandidat",
      status: "upcoming"
    },
    {
      icon: <CheckCircle size={24} />,
      date: "29 Oktober",
      title: "Pengumuman Akhir",
      description: "Staff IMADIBA 2024/2025",
      status: "upcoming"
    }
  ];

  return (
    <section className="py-20 bg-gradient-to-b from-white to-gray-50">
      <div className="container mx-auto px-4">
        {/* Header */}
        <div className="text-center mb-16">
          <h2 className="text-4xl font-bold text-gray-800 mb-4">
            Informasi Pendaftaran
          </h2>
          <div className="w-24 h-1 bg-[#fdc259] mx-auto rounded-full mb-6"></div>
          <p className="text-gray-600 text-lg max-w-2xl mx-auto">
            Timeline pendaftaran Staff IMADIBA periode 2024/2025
          </p>
        </div>

        {/* Timeline Steps */}
        <div className="max-w-6xl mx-auto">
          <div className="relative flex flex-col md:flex-row justify-between items-start md:items-center gap-8 md:gap-0">
            {/* Connecting Line */}
            <div className="hidden md:block absolute top-1/2 left-0 right-0 h-1 bg-gray-200 -translate-y-1/2 z-0">
              <div className="h-full bg-[#fdc259] rounded-full" style={{ width: '25%' }}></div>
            </div>

            {/* Steps */}
            {steps.map((step, index) => (
              <div 
                key={index}
                className="relative flex flex-col items-center text-center z-10 w-full md:w-auto"
              >
                {/* Step Circle */}
                <div className={`w-16 h-16 rounded-full flex items-center justify-center mb-4 transition-all duration-300
                  ${step.status === 'completed' ? 'bg-green-500 text-white' : 
                    step.status === 'active' ? 'bg-[#fdc259] text-white' : 
                    'bg-white text-gray-400 border-2 border-gray-200'}
                  ${step.status === 'active' ? 'shadow-lg scale-110' : ''}
                `}>
                  {step.icon}
                </div>

                {/* Content */}
                <div className={`transition-all duration-300 ${step.status === 'active' ? 'scale-105' : ''}`}>
                  <span className={`inline-block px-4 py-1 rounded-full text-sm font-semibold mb-2
                    ${step.status === 'completed' ? 'bg-green-100 text-green-600' : 
                      step.status === 'active' ? 'bg-[#fff3d9] text-[#fdc259]' : 
                      'bg-gray-100 text-gray-500'}
                  `}>
                    {step.date}
                  </span>
                  <h3 className="text-xl font-bold text-gray-800 mb-1">{step.title}</h3>
                  <p className="text-gray-600">{step.description}</p>

                  {step.hasButton && (
                    <button
                      onClick={() => window.open('https://docs.google.com/spreadsheets', '_blank')}
                      className="mt-4 inline-flex items-center gap-2 bg-[#fdc259] text-white px-6 py-2 rounded-full font-medium hover:bg-[#fdcd70] transition-all duration-300 shadow-md hover:shadow-lg hover:-translate-y-1"
                    >
                      Lihat Spreadsheet
                      <ChevronRight size={18} />
                    </button>
                  )}
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </section>
  );
};

export default RegistrationSection;
