import React, { useState, useEffect } from 'react';
import Footer from '../components/Footer';
import { ArrowRight, Award, BookOpen, Briefcase, ChevronDown, FileText, Users, ArrowUpRight, CalendarCheck } from 'lucide-react';

const Home = () => {
  const [scrollY, setScrollY] = useState(0);
  const [isVisible, setIsVisible] = useState({
    cards: false,
    updates: false
  });

  useEffect(() => {
    const handleScroll = () => {
      setScrollY(window.scrollY);

      const cardsSection = document.getElementById('info-cards');
      const updatesSection = document.getElementById('latest-updates');

      if (cardsSection) {
        const cardPosition = cardsSection.offsetTop - window.innerHeight + 200;
        setIsVisible(prev => ({
          ...prev,
          cards: window.scrollY > cardPosition
        }));
      }

      if (updatesSection) {
        const updatePosition = updatesSection.offsetTop - window.innerHeight + 200;
        setIsVisible(prev => ({
          ...prev,
          updates: window.scrollY > updatePosition
        }));
      }
    };

    window.addEventListener('scroll', handleScroll);
    handleScroll();

    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const parallaxOffset = -scrollY * 0.3;

  const timelineSteps = [
    {
      icon: <FileText className="w-6 h-6" />,
      title: "Pendaftaran Staff IMADIBA",
      date: "21-24 Oktober 2024",
      status: "Selesai",
      description: "Pengumpulan berkas dan formulir pendaftaran telah ditutup"
    },
    {
      icon: <Users className="w-6 h-6" />,
      title: "Pengumuman Lolos Berkas & Jadwal Wawancara",
      date: "25 Oktober 2024",
      status: "Sedang Berlangsung",
      description: "Pengumuman peserta yang lolos seleksi berkas",
      current: true
    },
    {
      icon: <CalendarCheck className="w-6 h-6" />,
      title: "Wawancara",
      date: "26-27 Oktober 2024",
      status: "Akan Datang",
      description: "Sesi wawancara dengan pengurus IMADIBA"
    },
    {
      icon: <Award className="w-6 h-6" />,
      title: "Pengumuman Staff IMADIBA",
      date: "29 Oktober 2024",
      status: "Akan Datang",
      description: "Pengumuman resmi staff IMADIBA 2024/2025"
    }
  ];

  return (
    <div className="min-h-screen">
      {/* Hero Section */}
      <section className="relative h-screen bg-gradient-to-r from-blue-900 to-blue-700 text-white overflow-hidden rounded-lg">
        <div className="absolute inset-0">
          <div className="bg-black opacity-50 absolute inset-0 rounded-lg"></div>
        </div>

        <div className="relative z-10 container mx-auto px-4 h-full flex flex-col justify-center items-center text-center">
          <div className="animate-fadeIn opacity-0" style={{ animation: 'fadeIn 1s forwards' }}>
            <h1 className="text-5xl font-bold mb-2 tracking-tight">
              Ikatan Mahasiswa Diponegoro Batang
            </h1>
          </div>

          <div className="my-8 transform transition-all duration-1000 hover:scale-105">
            <img 
              src="arahpulang.png"
              alt="Logo Arah Pulang" 
              className="h-64 drop-shadow-2xl animate-float"
            />
          </div>

          <div className="space-y-4 animate-slideUp opacity-0" style={{ animation: 'slideUp 1s 0.5s forwards' }}>
            <h2 className="text-4xl font-extrabold text-white">
              KABINET ARAH PULANG
            </h2>
            <p className="text-xl font-light italic mb-6">
              "Menuju Arah, Kembali Pulang"
            </p>
          </div>

          {/* Announcement Section */}
          <div className="mt-12 bg-white bg-opacity-95 py-8 px-6 rounded-xl shadow-xl w-full max-w-xl text-center">
            <div className="bg-green-50 text-green-700 px-4 py-2 rounded-lg mb-6 inline-block">
              <span className="font-medium">Pengumuman Sudah Dirilis!</span>
            </div>
            <h2 className="text-3xl font-bold text-gray-900 mb-2">
              Pengumuman Lolos Berkas & Jadwal Wawancara
            </h2>
            <div className="w-32 h-1 bg-[#fdc259] mx-auto rounded-full mb-6"></div>
            <p className="text-lg text-gray-600 mb-8">
              Selamat kepada para kandidat yang telah lolos tahap seleksi berkas. 
              Silahkan cek jadwal wawancara Anda melalui link di bawah ini.
            </p>
            <div className="space-y-4">
              <button
                onClick={() => window.open('https://docs.google.com/spreadsheets', '_blank')}
                className="bg-[#fdc259] text-gray-800 px-8 py-3 rounded-full font-semibold hover:bg-[#fdcd70] transition-all duration-300 inline-flex items-center gap-2 group"
              >
                Lihat Hasil Seleksi
                <ArrowUpRight className="w-5 h-5 transform transition-transform group-hover:translate-x-1 group-hover:-translate-y-1" />
              </button>
              <p className="text-sm text-gray-500 mt-4">
                *Pastikan untuk mengecek email Anda untuk informasi lebih lanjut
              </p>
            </div>
          </div>

          {/* Timeline Steps */}
          <div className="mt-12 w-full max-w-4xl">
            <div className="grid grid-cols-4 gap-4">
              {timelineSteps.map((step, index) => (
                <div key={index} className="relative">
                  <div className={`h-2 absolute top-4 left-1/2 w-full ${
                    index < timelineSteps.length - 1 ? 'bg-gray-200' : 'bg-transparent'
                  }`}></div>
                  <div className={`relative flex flex-col items-center ${
                    step.current ? 'scale-110 transition-transform duration-300' : ''
                  }`}>
                    <div className={`w-10 h-10 rounded-full flex items-center justify-center z-10 ${
                      step.status === 'Selesai' ? 'bg-green-100 text-green-600' :
                      step.status === 'Sedang Berlangsung' ? 'bg-blue-100 text-blue-600' :
                      'bg-gray-100 text-gray-600'
                    }`}>
                      {step.icon}
                    </div>
                    <div className="text-xs text-white mt-2 font-medium">{step.date}</div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </section>

      {/* Rest of the sections remain the same */}
      {/* Info Cards Section */}
      <section id="info-cards" className="py-20 bg-gradient-to-b from-gray-50 to-white">
        {/* ... (keep existing info cards section) ... */}
      </section>

      {/* Latest Updates Section */}
      <section id="latest-updates" className="py-20 bg-white">
        {/* ... (keep existing latest updates section) ... */}
      </section>

      <style jsx>{`
        @keyframes fadeIn {
          from { opacity: 0; }
          to { opacity: 1; }
        }

        @keyframes slideUp {
          from {
            opacity: 0;
            transform: translateY(20px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }

        @keyframes float {
          0% { transform: translateY(0px); }
          50% { transform: translateY(-10px); }
          100% { transform: translateY(0px); }
        }

        .animate-float {
          animation: float 3s ease-in-out infinite;
        }
      `}</style>

      <Footer />
    </div>
  );
};

export default Home;
